import './App.css';
import axios from 'axios';
import useState from 'react';

// const api = axios.create({
//   baseURL: "https://run.mocky.io/v3/9ddcc153-4ecb-4420-9c62-0e0a204e25b8",
// });

function App() {
  const [quote, setQuote] = useState('');
  const getQuote = () =>{
    axios.get("http://api.quotable.io/random")
    .then(res => {
      console.log(res.data.content);
    setQuote(res.data);
    // console.log('vhghj',setQuote(res.data));
    }).catch(err => {
      console.log(err);

    });
  };
  return (
    <div>
    <button onClick={getQuote}>Get Quote</button>
    { quote && <p>{quote}</p> }
    </div>

  );
}
export default App;
